/* Organize layer/source/severity in a single 32-bit word */
#define BSL_LAYER_SHFT          24
#define BSL_LAYER_MASK          0xff
#define BSL_SOURCE_SHFT         8
#define BSL_SOURCE_MASK         0xffff
#define BSL_SEVERITY_SHFT       0
#define BSL_SEVERITY_MASK       0xff

/* Get elements of packed meta data */
#define BSL_LAYER_GET(chk_) \
    (((chk_) >> BSL_LAYER_SHFT) & BSL_LAYER_MASK)
#define BSL_SOURCE_GET(chk_) \
    (((chk_) >> BSL_SOURCE_SHFT) & BSL_SOURCE_MASK)
#define BSL_SEVERITY_GET(chk_) \
    (((chk_) >> BSL_SEVERITY_SHFT) & BSL_SEVERITY_MASK)

/* Encode elements for packed meta data */
#define BSL_LAYER_ENC(layer_) \
    ((layer_) << BSL_LAYER_SHFT)
#define BSL_SOURCE_ENC(source_) \
    ((source_) << BSL_SOURCE_SHFT)
#define BSL_SEVERITY_ENC(severity_) \
    ((severity_) << BSL_SEVERITY_SHFT)

/* Severity defines for LOG_* macros */
#define BSL_FATAL               BSL_SEVERITY_ENC(BSL_SEV_FATAL)
#define BSL_ERROR               BSL_SEVERITY_ENC(BSL_SEV_ERROR)
