                            (READ_CLMIB_RX_EEE_LPI_EVENT_COUNTERr(unit, port, &val64));
                        _BCM_PORT_IF_ERROR_RETURN_WITH_UNLOCK
                            (READ_CLMIB_TX_EEE_LPI_DURATION_COUNTERr(unit, port, &val64));
