                        soc_reg_field_set(unit, GE0_EEE_CONFIGr, &val,
                            COUNTER_ROLLOVERf, 1);
                        _BCM_PORT_IF_ERROR_RETURN_WITH_UNLOCK
                                (WRITE_GE0_EEE_CONFIGr(unit, port, val));
                        _BCM_PORT_IF_ERROR_RETURN_WITH_UNLOCK
                                (WRITE_GE1_EEE_CONFIGr(unit, port, val));
