#ifndef _PM12X10_XGS_H__H_
#define _PM12X10_XGS_H__H_

#include <phymod/phymod.h>
#include <phymod/phymod_diagnostics.h>
#include <soc/portmod/portmod_defs.h>
/*Add new pm.*/
int pm12x10_xgs_pm_init(int unit, const portmod_pm_create_info_internal_t* pm_add_info, int wb_buffer_index, pm_info_t pm_info);

/*Release PM resources*/
int pm12x10_xgs_pm_destroy(int unit, pm_info_t pm_info);

/*Add new port*/
int pm12x10_xgs_port_attach(int unit, int port, pm_info_t pm_info, const portmod_port_add_info_t* add_info);

/*Port remove in PM level*/
int pm12x10_xgs_port_detach(int unit, int port, pm_info_t pm_info);

/*set/get interrupt enable value. */
int pm12x10_xgs_port_interrupt_enable_set(int unit, int port, pm_info_t pm_info, int intr_type, uint32 val);
int pm12x10_xgs_port_interrupt_enable_get(int unit, int port, pm_info_t pm_info, int intr_type, uint32* val);

/*get interrupt status value. */
int pm12x10_xgs_port_interrupt_get(int unit, int port, pm_info_t pm_info, int intr_type, uint32* val);

/*get interrupt value array. */
int pm12x10_xgs_port_interrupts_get(int unit, int port, pm_info_t pm_info, int arr_max_size, uint32* intr_arr, uint32* size);

#endif /*_PM12X10_XGS_H_*/
