#ifndef _SOC_SBX_ALLENUM_H
#define _SOC_SBX_ALLENUM_H


#ifndef EXTERN
# ifdef __cplusplus
#  define EXTERN extern "C"
# else
#  define EXTERN extern
# endif
#endif

/****************************************************************
 *
 * BCM Chip Support options.
 *
 * If NO_BCM_<chip> is defined, do not define BCM_<chip>.  This
 * avoids adding support for the chip.  NO_BCM_<chip> can be added
 * to Make.local.
 ****************************************************************/

#ifdef BCM_SBX_SUPPORT
#if !defined(NO_BCM_QE2000_A0)
#if !defined(NO_BCM_QE2000_A1)
#define BCM_QE2000_A1
#endif
#if !defined(NO_BCM_QE2000_A2)
#define BCM_QE2000_A2
#endif
#if !defined(NO_BCM_QE2000_A3)
#define BCM_QE2000_A3
#endif
#endif
#if !defined(NO_BCM_BME3200_A0)
#define BCM_BME3200_A0
#endif
#if !defined(NO_BCM_BME3200_B0)
#define BCM_BME3200_B0
#endif
#if !defined(NO_BCM_BM9600_A0)
#define BCM_BM9600_A0
#if !defined(NO_BCM_BM9600_A1)
#define BCM_BM9600_A1
#endif
#if !defined(NO_BCM_BM9600_B0)
#define BCM_BM9600_B0
#endif
#endif
#if !defined(NO_BCM_FE2000_A0)
#define BCM_FE2000_A0
#endif
#endif

#if defined(BCM_QE2000_A1) || defined(BCM_QE2000_A2) || defined(BCM_QE2000_A3)
#if   96 > SOC_MAX_NUM_PORTS
#undef  SOC_MAX_NUM_PORTS
#define SOC_MAX_NUM_PORTS       96
#endif
#endif /* BCM_QE2000_A1 || BCM_QE2000_A2 || BCM_QE2000_A3 */

#if defined(BCM_BME3200_A0) || defined(BCM_BME3200_B0)
#if   40 > SOC_MAX_NUM_PORTS
#undef  SOC_MAX_NUM_PORTS
#define SOC_MAX_NUM_PORTS       40
#endif
#endif /* BCM_BME3200_A0 || BCM_BME3200_B0 */

#if defined(BCM_BM9600_A0) || defined(BCM_BM9600_A1) || defined(BCM_BM9600_B0)
#if   96 > SOC_MAX_NUM_PORTS
#undef  SOC_MAX_NUM_PORTS
#define SOC_MAX_NUM_PORTS       96
#endif
#endif /* BCM_BM9600_A0 */

#if defined(BCM_FE2000_A0) || defined(BCM_FE2000_A1)
#if   64 > SOC_MAX_NUM_PORTS
#undef  SOC_MAX_NUM_PORTS
#define SOC_MAX_NUM_PORTS       64
#endif
#endif /* BCM_FE2000_A0 || BCM_FE2000_A1 */

#endif	/* !_SOC_SBX_ALLENUM_H */
