  soc_petra_pp_PETRA_PP_MACT_MATCH_RULE_clear(&prm_rule);
  prm_range.entries_to_act = UI_PETRA_PP_SCREEN_LINES;  
  prm_range.entries_to_scan = 40000;
  prm_nof_entries = 0;
  total_entries = 0;
  flavor = 1;
  prm_rule.including_dynamic = TRUE;
  prm_rule.including_static = TRUE;
  prm_rule.dest.dest_type = SOC_SAND_PP_DEST_SINGLE_PORT;

                                                                                                     
