  send_string_to_screen(" ---> soc_petra_pp_mact_entry_get_block - SUCCEEDED", TRUE);                                  

    goto exit;
                                                                                     
  goto exit;                                                                                         
exit:                                                                                                
  return ui_ret;                                                                                     
