/********************************************************************                
 *  Function handler: print_block (section fec_table)
 ********************************************************************/               
#endif /*PROG_PORT*/
int                                                                                                  
  ui_petra_pp_api_fec_table_get_block(                                                                             
    CURRENT_LINE *current_line                                                                       
  )                                                                                                  
{                                                                                                    
  uint32                                                                                           
    ret;                                                                                             
  SOC_PETRA_PP_FEC_TABLE_ENTRY                                                                                            
    prm_fec_table[SOC_PETRA_PP_UI_BLOCK_SIZE];
  SOC_PETRA_PP_BLOCK_RANGE
    prm_range;
  uint32                                                                                            
    prm_nof_entries,
    flavors;
  SOC_PETRA_PP_FEC_MATCH_RULE
    prm_match_rule;
  uint32
    total_entries;
  SOC_PETRA_PP_FEC_TABLE_ENTRY_PAYLOAD   
    fec_info;
  uint8
    print_entries;
  char
    user_msg[5] = "";
  uint32 
    con;

                                                                                                     
  UI_MACROS_INIT_FUNCTION("ui_petra_pp_api_fec_table");                                                  
  soc_sand_proc_name = "soc_petra_pp_fec_table_print_block";                                                              
                                                                                                     
  unit = soc_petra_get_default_unit(); 
  soc_petra_pp_PETRA_PP_FEC_MATCH_RULE_clear(&prm_match_rule);
  soc_petra_pp_PETRA_PP_BLOCK_RANGE_clear(&prm_range);
  soc_petra_pp_PETRA_PP_FEC_TABLE_ENTRY_PAYLOAD_clear(&fec_info);

 /*
  * defaults
  */
  prm_match_rule.match_mask = SOC_PETRA_PP_FEC_TABLE_MATCH_RULE_ALL;
            
  prm_range.entries_to_act = UI_PETRA_PP_SCREEN_LINES;
  prm_range.entries_to_scan = SOC_PETRA_PP_FEC_TABLE_SIZE;
  flavors = SOC_PETRA_PP_FEC_TABLE_PRINT_ALL;

  /* Get parameters */                                                                                
  if(!search_param_val_pairs(current_line,&match_index,PARAM_PETRA_PP_FEC_TABLE_PRINT_BLOCK_PRINT_BLOCK_FEC_TABLE_UC_ID,1))          
  {                                                                                                  
    UI_MACROS_GET_NUMMERIC_VAL(PARAM_PETRA_PP_FEC_TABLE_PRINT_BLOCK_PRINT_BLOCK_FEC_TABLE_UC_ID);                             
    UI_MACROS_CHECK_GET_VAL_OF_ERROR;                                                                
    if (!(param_val->value.ulong_value))
    {
      prm_match_rule.match_mask &= ~(SOC_PETRA_PP_FEC_TABLE_MATCH_RULE_UC);
    }
  }                                                                                                  
  if(!search_param_val_pairs(current_line,&match_index,PARAM_PETRA_PP_FEC_TABLE_PRINT_BLOCK_PRINT_BLOCK_FEC_TABLE_MC_ID,1))          
  {                                                                                                  
    UI_MACROS_GET_NUMMERIC_VAL(PARAM_PETRA_PP_FEC_TABLE_PRINT_BLOCK_PRINT_BLOCK_FEC_TABLE_MC_ID);                             
    UI_MACROS_CHECK_GET_VAL_OF_ERROR;    
    if (!(param_val->value.ulong_value))
    {
      prm_match_rule.match_mask &= ~(SOC_PETRA_PP_FEC_TABLE_MATCH_RULE_MC);
    }
  }                                                                                                  

  if(!search_param_val_pairs(current_line,&match_index,PARAM_PETRA_PP_FEC_TABLE_PRINT_BLOCK_PRINT_BLOCK_FEC_TABLE_PROG_PORT_ID,1))          
  {                                                                                                  
    UI_MACROS_GET_NUMMERIC_VAL(PARAM_PETRA_PP_FEC_TABLE_PRINT_BLOCK_PRINT_BLOCK_FEC_TABLE_PROG_PORT_ID);                             
    UI_MACROS_CHECK_GET_VAL_OF_ERROR; 
    if (!(param_val->value.ulong_value))
    {
      prm_match_rule.match_mask &= ~(SOC_PETRA_PP_FEC_TABLE_MATCH_RULE_PROG);
    }
  }         

  if(!search_param_val_pairs(current_line,&match_index,PARAM_PETRA_PP_FEC_TABLE_PRINT_BLOCK_PRINT_BLOCK_ITER_ID,1))          
  {                                                                                                  
    UI_MACROS_GET_NUMMERIC_VAL(PARAM_PETRA_PP_FEC_TABLE_PRINT_BLOCK_PRINT_BLOCK_ITER_ID);                             
    UI_MACROS_CHECK_GET_VAL_OF_ERROR;                                                                
    prm_range.iter = (uint32)param_val->value.ulong_value;
  }                                                                                                  


  total_entries = 0;
  print_entries = TRUE;
  while (prm_range.iter < SOC_PETRA_PP_FEC_TABLE_SIZE && prm_range.entries_to_act && prm_range.entries_to_scan)
  {
      /* Call function */                                                                                
    ret = soc_petra_pp_fec_table_get_block(
            unit,
            &prm_match_rule,
            &prm_range,
            prm_fec_table,
            &prm_nof_entries
          );
    if (soc_sand_get_error_code_from_error_word(ret) != SOC_SAND_OK)                                                                                        
    {                                                                                                
      send_string_to_screen(" *** soc_petra_pp_fec_table_print_block - FAIL", TRUE);                       
      soc_petra_disp_result(ret, "soc_petra_pp_fec_table_print_block");                         
      goto exit;                                                                                     
    }                                                                                                

    total_entries += prm_nof_entries;
    if (print_entries == FALSE)
    {
      continue;
    }
    ret = soc_petra_pp_fec_table_print_block(
            unit,
            prm_fec_table,
            prm_nof_entries,
            flavors
          );
    if (soc_sand_get_error_code_from_error_word(ret) != SOC_SAND_OK)                                                                                        
    {                                                                                                
      send_string_to_screen(" *** soc_petra_pp_fec_table_print_block - FAIL", TRUE);                       
      soc_petra_disp_result(ret, "soc_petra_pp_fec_table_print_block");                         
      goto exit;                                                                                     
    }                                                                                                

    if (prm_range.iter < SOC_PETRA_PP_FEC_TABLE_SIZE)
    {
      soc_sand_os_printf( "Hit Space to continue/Any Key to Abort.\n\r");
      con = ask_get(user_msg,EXPECT_CONT_ABORT,FALSE,TRUE) ;
      if (!con)
      {
        print_entries = FALSE;
      }
    }
  }
  send_string_to_screen("--> nof_entries:", TRUE);                   
  soc_sand_os_printf( "total_entries: %lu\n\r",total_entries);
  send_string_to_screen(" ---> soc_petra_pp_fec_table_get_block - SUCCEEDED", TRUE);                                  

  goto exit;                                                                                         
exit:                                                                                                
  return ui_ret;                                                                                     
}                                                                                                    
                                                                                                     
