/********************************************************************                
 *  Function handler: ace_get_block (section acl)
 ********************************************************************/               
int                                                                                                  
  ui_petra_pp_api_acl_ace_get_block(                                                                             
    CURRENT_LINE *current_line                                                                       
  )                                                                                                  
{                                                                                                    
  uint32                                                                                           
    ret;                                                                                             
  uint32                                                                                            
    prm_acl_ndx;
  SOC_PETRA_PP_BLOCK_RANGE                                                                                            
    prm_block_range;                                                                                             
  SOC_PETRA_PP_ACL_ACE_INFO                                                                                            
    prm_ace_block[SOC_PETRA_PP_UI_BLOCK_SIZE];                                                                                             
  SOC_PETRA_PP_ACL_ACTION_INFO                                                                                            
    prm_actions_block[SOC_PETRA_PP_UI_BLOCK_SIZE];  
  SOC_PETRA_PP_TCAM_PLACE_ID    
    places[SOC_PETRA_PP_UI_BLOCK_SIZE];
  uint32                                                                                            
    prm_nof_entries,
    total_entries;
  uint8
    print_entries=TRUE;
  char
    user_msg[5] = "";
  uint32 
    con;
                                                                                                     
  UI_MACROS_INIT_FUNCTION("ui_petra_pp_api_acl");                                                  
  soc_sand_proc_name = "soc_petra_pp_acl_ace_get_block";                                                              
                                                                                                     
  unit = soc_petra_get_default_unit(); 

  prm_block_range.entries_to_act = UI_PETRA_PP_SCREEN_LINES;  
  prm_block_range.entries_to_scan = 20000;
  prm_block_range.iter = SOC_PETRA_PP_TCAM_LIST_ITER_BEGIN(SOC_PETRA_PP_TCAM_ENTRY_USE_ACL_L2);
  prm_nof_entries = 0;
  total_entries = 0;


  /* Get parameters */                                                                                
  if(!search_param_val_pairs(current_line,&match_index,PARAM_PETRA_PP_ACL_ACE_GET_BLOCK_ACE_GET_BLOCK_ACL_NDX_ID,1))          
  {                                                                                                  
    UI_MACROS_GET_NUMMERIC_VAL(PARAM_PETRA_PP_ACL_ACE_GET_BLOCK_ACE_GET_BLOCK_ACL_NDX_ID);                             
    UI_MACROS_CHECK_GET_VAL_OF_ERROR;                                                                
    prm_acl_ndx = (uint32)param_val->value.ulong_value;
  }                                                                                                  
  else                                                                                               
  {                                                                                                  
    send_string_to_screen(" *** SW error - expecting parameter acl_ndx after ace_get_block***", TRUE);                                                                
    goto exit;                                                                                                  
  }    
  while (prm_block_range.iter != SOC_PETRA_PP_TCAM_LIST_ITER_END(0) && prm_block_range.entries_to_act && prm_block_range.entries_to_scan)
  {

    ret = soc_petra_pp_acl_ace_get_block(
            unit,
            prm_acl_ndx,
            &prm_block_range,
            prm_ace_block,
            places,
            prm_actions_block,
            &prm_nof_entries
          );
    if (soc_sand_get_error_code_from_error_word(ret) != SOC_SAND_OK)                                                                                        
    {                                                                                                
      send_string_to_screen(" *** soc_petra_pp_acl_ace_get_block - FAIL", TRUE);                       
      soc_petra_disp_result(ret, "soc_petra_pp_acl_ace_get_block");                         
      goto exit;                                                                                     
    }    
    total_entries += prm_nof_entries;
    if (prm_nof_entries == 0)
    {
      break;
    }
    if (print_entries == FALSE)
    {
      continue;
    }

    ret = soc_petra_pp_acl_table_print(
            unit,
            prm_acl_ndx,
            prm_ace_block,
            places,
            prm_actions_block,
            prm_nof_entries,
            0
          );
    if (soc_sand_get_error_code_from_error_word(ret) != SOC_SAND_OK)                                                                                        
    {                                                                                                
      send_string_to_screen(" *** soc_petra_pp_acl_table_print - FAIL", TRUE);                       
      soc_petra_disp_result(ret, "soc_petra_pp_acl_table_print");                         
      goto exit;                                                                                     
    }

    if (prm_block_range.iter != SOC_PETRA_PP_TCAM_LIST_ITER_END(0) && prm_nof_entries >= prm_block_range.entries_to_act)
    {
      soc_sand_os_printf( "Hit Space to continue/Any Key to Abort.\n\r");
      con = ask_get(user_msg,EXPECT_CONT_ABORT,FALSE,TRUE) ;
      if (!con)
      {
        print_entries = FALSE;
      }
    }

  }

  send_string_to_screen(" ---> soc_petra_pp_acl_ace_get_block - SUCCEEDED", TRUE);                                  
  send_string_to_screen("--> nof_entries:", TRUE);                   
  soc_sand_os_printf( "nof_entries: %lu\n\r",total_entries);

  goto exit;

  if (prm_ace_block[0].type == SOC_PETRA_PP_ACL_ACE_TYPE_L2)
  {
    send_string_to_screen(" ---------------------------------------------------------------------------------------------------------------------------------", TRUE);                   
    send_string_to_screen("|                                               ACL Table                                                                         |", TRUE);                   
    send_string_to_screen("|---------------------------------------------------------------------------------------------------------------------------------|", TRUE);                   
    send_string_to_screen("|                         ACE                                                       |     ACTION                                  |", TRUE);                   
    send_string_to_screen("|---------------------------------------------------------------------------------------------------------------------------------|", TRUE);                   
  }
  else
  {
    send_string_to_screen(" ---------------------------------------------------------------------------------------------------------------------------------", TRUE);                   
    send_string_to_screen("|                                               ACL Table                                                                         |", TRUE);                   
    send_string_to_screen("|---------------------------------------------------------------------------------------------------------------------------------|", TRUE);                   
    send_string_to_screen("|                         ACE                                                       |     ACTION                                  |", TRUE);                   
    send_string_to_screen("|---------------------------------------------------------------------------------------------------------------------------------|", TRUE);                   
    send_string_to_screen("|indx |  Port    |                         ACE                                                       |     ACTION                                  |", TRUE);                   
    send_string_to_screen("|indx |type| Num | S-VLAN | C-VLAN |    SA  MAC        |    DA  MAC        | Eth Code|                                 |     ACTION                                  |", TRUE);                   
}

                                                                                      
exit:                                                                                                
  return ui_ret;                                                                                     
}                                                                                                    
                                                                                                     
