/********************************************************************                
 *  Function handler: ipv6_route_get_block (section ipv6)
 ********************************************************************/               
int                                                                                                  
  ui_petra_pp_api_ipv6_route_get_block(                                                                             
    CURRENT_LINE *current_line                                                                       
  )                                                                                                  
{                                                                                                    
  uint32                                                                                           
    ret;                                                                                             
  uint32                                                                                            
    prm_vrf_id = 0;                                                                                             
  SOC_PETRA_PP_BLOCK_RANGE                                                                                            
    prm_block_range;                                                                                             
  SOC_PETRA_PP_IPV6_ROUTE_ENTRY                                                                                            
    prm_route_table[SOC_PETRA_PP_UI_BLOCK_SIZE];                                                                                             
  uint32                                                                                            
    prm_nof_entries,
    flavors;
  char
    user_msg[5] = "";
  uint32 
    con;
  uint32
    total_entries;
  uint8
    print_entries;
                                                                                                     
  UI_MACROS_INIT_FUNCTION("ui_petra_pp_api_ipv6");                                                  
  soc_sand_proc_name = "soc_petra_pp_ipv6_route_get_block";                                                              
                                                                                                     
  unit = soc_petra_get_default_unit(); 
  prm_block_range.entries_to_act = UI_PETRA_PP_SCREEN_LINES;
  prm_block_range.entries_to_scan = 10000;
  prm_block_range.iter = SOC_PETRA_PP_TCAM_LIST_ITER_BEGIN(0);
  
  flavors = 0;
                                                                                                     
  /* Get parameters */                                                                                
  if(!search_param_val_pairs(current_line,&match_index,PARAM_PETRA_PP_IPV6_ROUTE_GET_BLOCK_IPV6_ROUTE_GET_BLOCK_VRF_ID_ID,1))          
  {                                                                                                  
    UI_MACROS_GET_NUMMERIC_VAL(PARAM_PETRA_PP_IPV6_ROUTE_GET_BLOCK_IPV6_ROUTE_GET_BLOCK_VRF_ID_ID);                             
    UI_MACROS_CHECK_GET_VAL_OF_ERROR;                                                                
    prm_vrf_id = (uint32)param_val->value.ulong_value;
  }                                                                                                  

  if(!search_param_val_pairs(current_line,&match_index,PARAM_PETRA_PP_IPV6_ROUTE_GET_BLOCK_IPV6_ROUTE_GET_BLOCK_BLOCK_RANGE_ID,1))          
  {                                                                                                  
    UI_MACROS_GET_NUMMERIC_VAL(PARAM_PETRA_PP_IPV6_ROUTE_GET_BLOCK_IPV6_ROUTE_GET_BLOCK_BLOCK_RANGE_ID);                             
    UI_MACROS_CHECK_GET_VAL_OF_ERROR;  

    if (param_val->value.ulong_value)
    {
      flavors = SOC_PETRA_PP_IPV6_PRINT_NEXT_HOP_INFO;
    }
    else
    {
      flavors = 0;
    }
  }             

  total_entries = 0;
  print_entries = TRUE;

  while (prm_block_range.iter !=  SOC_PETRA_PP_TCAM_LIST_ITER_END(0) && prm_block_range.entries_to_act && prm_block_range.entries_to_scan)
  {

    /* Call function */                                                                                
    ret = soc_petra_pp_ipv6_route_get_block(
            unit,
            prm_vrf_id,
            &prm_block_range,
            prm_route_table,
            &prm_nof_entries
          );
    if (soc_sand_get_error_code_from_error_word(ret) != SOC_SAND_OK)                                                                                        
    {                                                                                                
      send_string_to_screen(" *** soc_petra_pp_ipv6_route_get_block - FAIL", TRUE);                       
      soc_petra_disp_result(ret, "soc_petra_pp_ipv6_route_get_block");                         
      goto exit;                                                                                     
    }  

    if (prm_nof_entries == 0)
    {
      break;
    }

    total_entries += prm_nof_entries;
    if (print_entries == FALSE)
    {
      continue;
    }

    ret = soc_petra_pp_ipv6_print_block(
            unit,
            prm_vrf_id,
            prm_route_table,
            prm_nof_entries,
            flavors
          );

    if (prm_block_range.iter !=  SOC_PETRA_PP_TCAM_LIST_ITER_END(0) && prm_nof_entries >= prm_block_range.entries_to_act)
    {
      soc_sand_os_printf( "Hit Space to continue/Any Key to Abort.\n\r");
      con = ask_get(user_msg,EXPECT_CONT_ABORT,FALSE,TRUE) ;
      if (!con)
      {
        print_entries = FALSE;
      }
    }
  }
  soc_sand_os_printf( "total_entries: %lu\n\r",total_entries);

                                                                                                     
  goto exit;                                                                                         
exit:                                                                                                
  return ui_ret;                                                                                     
}                                                                                                    
                                                                                                     
